import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'

const Pagination = () => {
    const { page, changePageHandler, totalPages } = useContext(AppContext);
    return (
        <div className='w-full flex justify-center item'>
            <div className='flex justify-between w-11/12 max-w-[500px]'>
                {
                    page > 1 && (<button className='rounded-md border py-1  px-4'
                        onClick={() => changePageHandler(page - 1)}>Previous</button>
                    )}
                {
                    page < totalPages && (<button className='rounded-md border py-1 px-4'
                        onClick={() => changePageHandler(page + 1)}>Next</button>)
                }

                <p>page{page} of {totalPages}</p>
            </div>
        </div>)

}

export default Pagination
