import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'
import Spinner from './Spinner';
import './Blogs.css'

const Blogs = () => {
    // consume 
    const { posts, loading } = useContext(AppContext);
    console.log("printing inside blogs component");
    console.log(posts);
    return (
        <div className='w-11/12 max-w-[450px] py-3 justify-center flex flex-col gap-y-7'>
        {
        loading ?
                    (<Spinner />) :
                    (
                        posts.length === 0 ?
                            (<div><p>NO post found </p></div>) :
                            (posts.map((post) => (
                                <div key={post.id}>
                                    <p className='font-bold text-xs'>{post.title}</p>
                                    <p className='text-[10px]'>
                                        By <span className='italic'>{post.author}</span> on <span>{post.category}</span>
                                    </p>
                                    <p>Posted on {post.date}</p>
                                    <p>{post.content}</p>
                                    <div>
                                        {
                                            post.tags.map((tag,index) => {
                                                  return <span key={index}>{`#${tag}`}</span> })
                                        }</div>
                                </div>
                            )
                            )
                            )
                    )
         
          }
        </div>

    )
}

export default Blogs
