import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'

const Pagination =() =>{
    const { page, changePageHandler, totalPages } = useContext(AppContext);
    return (
        <div className=''>
            {
                page > 1 && (<button className='rounded-md border py-1 px-4' className='rounded-md border py-1 px-4' className='rounded-md border py-1 px-4'
                onClick={() => changePageHandler(page - 1)}>Previous</button>
                )}
            {
                page < totalPages && (<button onClick={() => changePageHandler(page + 1)}>Next</button>)
            }

            <p>page{page} of {totalPages}</p>
        </div>
    )
}

export default Pagination
