import React, { useContext } from 'react'
import { AppContext } from '../context/AppContext'
import Spinner from './Spinner';
import './Blogs.css'

const Blogs = () => {
    // consume 
    const { posts, loading } = useContext(AppContext);
    return (
        <div className='w-11/12 max-w-[450px] py-3 justify-center flex flex-col gap-y-7 mt-[66px] mb-[70px]'>
            { loading ?
                    (<Spinner />) :(
                        posts.length === 0 ?
                            (<div><p>NO post found </p></div>) :
                            (posts.map((post) => (
                                <div key={post.id}>
                                    <p className='font-bold text-xs'>{post.title}</p>
                                    <p className='text-[10px]'>
                                        By <span className='italic'>{post.author}</span> on <span className='underline font-bold'>{post.category}</span>
                                    </p>
                                    <p className='text-[16px]'>Posted on {post.date}</p>
                                    <p className='text-[16px] mt-3'>{post.content}</p>
                                    <div className='flex  gap-x-3'>
                                    {
                                        post.tags.map((tag, index) => {
                                         return <span key={index} className='underline text-blue-400 text-bold text-[8px]'>{`#${tag}`}</span>
                                        })
                                    }</div>
                                </div>
                            )
                        )
                    )
                )
            }
        </div>
    )
}
export default Blogs
